mapper.insert:
INSERT
		INTO
			REVIEW
			(
				REVIEW_NO
				,USER_NO
				,UC_SEQ
				,REVIEW_RATING
				,REVIEW_COMMENT
				,REVIEW_CREATE_DATE
				,FILE_PATH
				,CHANGE_NAME
				,REVIEW_STATUS
			)
	values
			(
				 SEQ_REVIEW.NEXTVAL
				,#{userNo}
				,#{ucSeq}
				,#{rating}
				,#{reviewComment}
				,SYSDATE
				,#{filePath}
				,#{changeName}
				,'Y'
			)

mapper.update:
UPDATE REVIEW
SET 
	 REVIEW_RATING = #{rating}
	,REVIEW_COMMENT = #{reviewComment}
WHERE
	REVIEW_NO = #{reviewNo}
